  image VARCHAR(255) NOT NULL,
  isDestroyed SMALLINT NOT NULL,
  description TEXT NOT NULL,
  deletedAt TIMESTAMP(6)
);

-- 2. CHARACTERS
DROP TABLE IF EXISTS characters CASCADE;
CREATE TABLE characters (
  id SERIAL PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  image VARCHAR(255) NOT NULL,
  ki VARCHAR(255) NOT NULL,
  maxKi VARCHAR(255) NOT NULL,
  race VARCHAR(32) NOT NULL,
  gender VARCHAR(16) NOT NULL,
  affiliation VARCHAR(32) NOT NULL,
  description TEXT NOT NULL,
  originPlanetId INTEGER REFERENCES planets(id),
  deletedAt TIMESTAMP(6)
);

-- 3. TRANSFORMATIONS
DROP TABLE IF EXISTS transformations CASCADE;
CREATE TABLE transformations (
  id SERIAL PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  image VARCHAR(255) NOT NULL,
  ki VARCHAR(255) NOT NULL,
  characterId INTEGER REFERENCES characters(id),
  deletedAt TIMESTAMP(6)
);

